#pragma once

// Define the return values of the HD map functions

// Operation successful
#define TX_HADMAP_OK 0

// Map handle error
#define TX_HADMAP_HANDLE_ERROR 1

// Map handle is valid
#define TX_HADMAP_HANDLE_OK 2

// Map data error
#define TX_HADMAP_DATA_ERROR 3

// Map data is valid
#define TX_HADMAP_DATA_OK 4

// Map data is empty
#define TX_HADMAP_DATA_EMPTY 5

// Parameter error
#define TX_HADMAP_PARAM_ERROR 6

// The data is not supported
#define TX_HADMAP_DATA_NOT_SUPPORT 7
